mapper.select:
select * from WP_memo
where memo_date BETWEEN to_date(#{startDate}, 'MMDDYYYY') AND to_date(#{endDate}, 'MMDDYYYY')+1
and day_check = #{day_check}

mapper.update:
update WP_memo set memo = #{memo}
where memo_date BETWEEN to_date(#{startDate}, 'MMDDYYYY') AND to_date(#{endDate}, 'MMDDYYYY')+1
and day_check = #{day_check}

mapper.delete:
delete from WP_memo
where memo_date BETWEEN to_date(#{startDate}, 'MMDDYYYY') AND to_date(#{endDate}, 'MMDDYYYY')+1
and day_check = #{day_check}
